parse_line_test() ->
    ok = linux_proc_stat:parse_line(<<"">>),
    Result =
        linux_proc_stat:parse_line(<<"cpu  32282702 38357 82864541 301409169352 3461306 0 261729 0 0 0">>),
    ExpectedOutput =
        {cpu,
         #{system => 82864541,
           user => 32282702,
           idle => 301409169352,
           irq => 0,
           nice => 38357,
           iowait => 3461306,
           softirq => 261729,
           steal => 0,
           guest => 0,
           guest_nice => 0}},
    ?assertEqual(ExpectedOutput, Result).

cpu_ratios_test() ->
    {cpu, Prev} =
        linux_proc_stat:parse_line(<<"cpu  32282702 38357 82864541 301409169352 3461306 0 261729 0 0 0">>),
    {cpu, Curr} =
        linux_proc_stat:parse_line(<<"cpu  32302735 38387 82880936 301537830817 3462800 0 261874 0 0 0">>),

    Delta = linux_proc_stat:cpu_delta(Prev, Curr),
    DeltaExpected =
        #{system => 16395,
          user => 20033,
          idle => 128661465,
          irq => 0,
          nice => 30,
          iowait => 1494,
          softirq => 145,
          steal => 0,
          guest => 0,
          guest_nice => 0},
    ?assertEqual(Delta, DeltaExpected),

    Ratios = linux_proc_stat:cpu_as_ratios(Delta),
    RatiosExpected =
        #{system => 1.2738971093001855e-4,
          user => 1.556570953986619e-4,
          idle => 0.999703985006569,
          irq => 0.0,
          nice => 2.3310102640442552e-7,
          iowait => 1.1608431114940391e-5,
          softirq => 1.1266549609547233e-6,
          steal => 0.0,
          guest => 0.0,
          guest_nice => 0.0},
    ?assertEqual(Ratios, RatiosExpected).
